beforeSaveCustomScript:
amount=quantity*price;
taxAmount=quantity*price*taxPercentage/100;
grandTotal=amount+taxAmount;

if (productId) {
    lineProductName=productName;
    name=string\concatenate(productName,' | Qty:',quantity,' | Price:',price,' | Amount:',amount);
} else {
    name=string\concatenate(lineProductName,' | Qty:',quantity,' | Price:',price,' | Amount:',amount);
}
